from quac import anyq, listq
from .preps import (prep__keys, prep__def)
from .sig import (getsigkws)
from .dun import (getnew, getini, getsup,)
from .ann import getannkws

# %% ../nbs/07_kws.ipynb 6
def newkws(obj: anyq = None) -> dict:
    return getsigkws(getnew(obj))

def inikws(obj: anyq = None) -> dict:
    return getsigkws(getini(obj))

def annkws(obj: anyq = None) -> dict:
    return getannkws(obj)

def supkws(obj: anyq = None) -> dict:
    return getsigkws(getnew(getsup(obj)))

def defkws(obj: anyq = None, **kwargs) -> dict:    
    return {k: kwargs.get(k, prep__def(__key=k, **kwargs)) for k in allkeys(obj, prep__keys(**kwargs))}

def nilkws(obj: anyq = None, keys: listq = None) -> dict:        
    return {k: None for k in allkeys(obj, keys)}

def allkeys(obj: anyq = None, keys: listq = None) -> list:
    keys = sorted(list(set(
        list(newkws(obj).keys()) + 
        list(inikws(obj).keys()) + 
        list(annkws(obj).keys()) + 
        (keys or [])
    )))
    return keys
